import eyeOpen from "../../../../static/眼睛-开@2x.png";
import eyeClose from "../../../../static/眼睛-闭@2x.png";
import axios from "axios";
import Hint from "../../comment/Hint";
import Loading from "../../comment/Loading";
export default {
  name: "LoginContent",
  data: function data() {
    return {
      isOpen: false,
      pwd: "",
      name: "",
      eyeImgUrl: eyeClose,
      hintTitle: "",
      showHint: false,
      showLoading: false
    };
  },
  components: {
    Hint: Hint,
    Loading: Loading
  },
  methods: {
    openCloseClick: function openCloseClick() {
      if (this.isOpen) {
        this.eyeImgUrl = eyeClose;
        this.isOpen = !this.isOpen;
        this.pwd = document.getElementsByClassName("pwdInput")[0].value;
      } else {
        this.eyeImgUrl = eyeOpen;
        this.isOpen = !this.isOpen;
        this.pwd = document.getElementsByClassName("pwdInput-pwd")[0].value;
      }
    },
    loginBtnClick: function loginBtnClick() {
      if (this.name.length == 0) {
        this.hintTitle = "请输入用户名";
        this.showHint = true;
      } else if (this.pwd.length == 0) {
        this.hintTitle = "请输入密码";
        this.showHint = true;
      } else {
        localStorage.clear();
        this.$router.push("/myCase");
        axios.get("/static/tsconfig.json").then(this.loginSucc);
      }
    },
    hideHint: function hideHint() {
      this.showHint = false;
    },
    loginSucc: function loginSucc(res) {
      console.log(res);
    }
  }
};
