export default {
  name: "Hint",
  props: {
    title: String,
    isShow: Boolean
  },
  data: function data() {
    return {
      timer: null,
      inShowTime: 0
    };
  },
  methods: {
    timeAdd: function timeAdd() {
      this.inShowTime = (this.inShowTime + 1) % 3;

      if (this.inShowTime == 0) {
        this.$emit("hideHint");
      }
    }
  },
  watch: {
    isShow: function isShow(val) {
      var _this = this;

      if (val === true) {
        this.timer = setInterval(function () {
          _this.timeAdd();
        }, 1000);
      } else {
        clearInterval(this.timer);
      }
    }
  }
};
