import Bscroll from "better-scroll";
import Loading from "../../comment/Loading";
import DialogBar from "../../comment/DialogBar";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "Checked",
  data: function data() {
    return {
      index: 1,
      pageSize: 10,
      isHaveList: false,
      checkeds: [],
      showLoading: false,
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  components: {
    Loading: Loading,
    DialogBar: DialogBar
  },
  props: {
    isShow: Boolean
  },
  methods: {
    cancel: function cancel() {
      this.isShowDialog = false;

      if (this.goToLogin) {
        var len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    checkClick: function checkClick(index) {
      console.log("go to payStatus");
      this.$router.push({
        name: "payStatus",
        params: this.checkeds[index]
      });
    },
    requestData: function requestData() {
      console.log("请求已审核");
      var self = this;
      self.showLoading = true;
      var param = {
        index: this.index,
        pageSize: this.pageSize
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      self.$axios({
        method: "POST",
        url: "/api/ace-app/waitingCheck/rmasChecked/query",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log(response);
        var data = response.data;
        var mydata = data.data;

        if (mydata.code == "duplicateLogin") {
          self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
          self.isShowDialog = true;
          self.goToLogin = true;
        } else {
          var list = mydata.list;

          if (list.length == 0 && self.index == 1) {
            self.isHaveList = false;
          } else {
            self.isHaveList = true;

            if (self.index == 1) {
              self.checkeds = [];
            }

            if (list.length > 0) {
              for (var i = 0; i < list.length; i++) {
                self.checkeds.push(list[i]);
              }
            }
          }
        }

        self.showLoading = false;
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    getTime: function getTime(time) {
      if (time != null) {
        return time.substring(0, 4) + "年" + time.substring(4, 6) + "月" + time.substring(6, 8) + "日";
      } else {
        return "";
      }
    }
  },
  watch: {
    isShow: function isShow(newVal) {
      var _this = this;

      if (newVal === true) {
        this.$nextTick(function () {
          var bscrollDom = _this.$refs.checked;
          _this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });

          _this.scroll.on("pullingUp", function () {
            if (!_this.showLoading) {
              console.log("上拉加载");
              _this.pageIndex++;

              _this.requestData();
            }

            _this.scroll.finishPullUp();
          });

          _this.scroll.on("pullingDown", function () {
            if (!_this.showLoading) {
              console.log("下拉刷新");
              _this.pageIndex = 1;

              _this.requestData();
            }

            _this.scroll.finishPullDown();
          });
        });
        this.requestData();
      }
    }
  }
};
