  watch: {
    beginUp: function beginUp(newVal) {
      var _this = this;

      if (newVal === true) {
        var succNum;

        (function () {
          //开始上传图片
          var self = _this;
          self.upErr = false;

          if (_this.imgFiles.length > 0) {
            console.log("开始上传图片");
            succNum = 0;

            for (var i = 0; i < self.imgFiles.length; i++) {
              if (self.upErr) break;
              var file = self.imgFiles[i];
              var param = new FormData();
              param.append("file", file, file.name);
              var config = {
                headers: {
                  "Content-Type": "multipart/form-data"
                }
              };
              var url = "api/ace-app/collectionPhoto/imageAdd?caseSubId=" + self.caseSubId;
              self.$axios.post(url, param, config).then(function (response) {
                console.log(response);
                var mydata = response.data;

                if (mydata.message == "上传成功") {
                  succNum++; //保存返回的URL

                  var _url = mydata.data;
                  self.imgUrls.push(_url);

                  if (succNum == _this.imgFiles.length) {
                    //图片上传完
                    self.$emit("photoUpFinish", self.imgUrls);
                  }
                } else {
                  self.upErr = true;
                  self.$emit("photoUpErr");
                }
              }).catch(function (error) {
                console.log(error);
                self.upErr = true;
                self.$emit("photoUpErr");
              });
            }
          } else {
            self.$emit("photoUpFinish", self.imgUrls);
          }
        })();
      }
    }
  },
