import AddNav from "./component/AddNav";
import AddInfo from "./component/AddInfo";
import AddPhoto from "./component/AddPhoto";
import AddVideo from "./component/AddVideo";
import AddAudio from "./component/AddAudio";
import DialogBar from "../comment/DialogBar";
import Loading from "../comment/Loading";
import Bscroll from "better-scroll";
import { Decrypt, Encrypt } from "../../utils";
export default {
  name: "AddRmas",
  data: function data() {
    return {
      clickSubmit: false,
      infoIsOK: false,
      photoUpIsOver: false,
      videoUpIsOver: false,
      audioUpIsOver: false,
      info: {},
      photoUrls: [],
      videoUrls: [],
      audioUrls: [],
      oneCase: {},
      myAddress: "定位失败",
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  components: {
    AddNav: AddNav,
    AddInfo: AddInfo,
    AddPhoto: AddPhoto,
    AddVideo: AddVideo,
    AddAudio: AddAudio,
    DialogBar: DialogBar,
    Loading: Loading
  },
  methods: {
    goBackToDetail: function goBackToDetail() {
      this.$router.back(-1);
    },
    submitClick: function submitClick() {
      this.clickSubmit = true;
    },
    cancel: function cancel() {
      this.clickSubmit = false;
      this.isShowDialog = false;

      if (this.goToLogin) {
        var len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    showDialog: function showDialog(content) {
      this.DialogContent = content;
      this.isShowDialog = true;
    },
    infoOK: function infoOK(info) {
      console.log("内容全部填写", info);
      this.info = info;
      this.infoIsOK = true;
    },
    photoUpFinish: function photoUpFinish(imgUrls) {
      console.log("图片上传完成" + imgUrls);
      this.photoUpIsOver = true;
      this.photoUrls = imgUrls;
    },
    photoUpErr: function photoUpErr() {
      this.clickSubmit = false;
      this.DialogContent = "图片上传失败";
      this.isShowDialog = true;
    },
    videoUpFinish: function videoUpFinish(videoUrls) {
      console.log("视频上传完成" + videoUrls);
      this.videoUpIsOver = true;
      this.videoUrls = videoUrls;
    },
    videoUpErr: function videoUpErr() {
      this.clickSubmit = false;
      this.DialogContent = "视频上传失败";
      this.isShowDialog = true;
    },
    audioUpFinish: function audioUpFinish(audioUrls) {
      console.log("音频上传完成" + audioUrls);
      this.audioUpIsOver = true;
      this.audioUrls = audioUrls;
      this.submit();
    },
    audioUpErr: function audioUpErr() {
      this.clickSubmit = false;
      this.DialogContent = "音频上传失败";
      this.isShowDialog = true;
    },
    submit: function submit() {
      //上传催记
      this.goToLogin = false;
      var self = this;
      var photoList = "";

      for (var i = 0; i < this.photoUrls.length; i++) {
        if (i == 0) {
          photoList = photoList + this.photoUrls[i];
        } else {
          photoList = photoList + "," + this.photoUrls[i];
        }
      }

      var videoList = "";

      for (var _i = 0; _i < this.videoUrls.length; _i++) {
        if (_i == 0) {
          videoList = videoList + this.videoUrls[_i];
        } else {
          videoList = videoList + "," + this.videoUrls[_i];
        }
      }

      var audioList = "";

      for (var _i2 = 0; _i2 < this.audioUrls.length; _i2++) {
        if (_i2 == 0) {
          audioList = audioList + this.audioUrls[_i2];
        } else {
          audioList = audioList + "," + this.audioUrls[_i2];
        }
      }

      self.info["photoList"] = photoList;
      self.info["videoList"] = videoList;
      self.info["audioList"] = audioList;
      self.info["caseSubId"] = self.oneCase.caseSubId;
      self.info["address"] = self.myAddress;
      var params = JSON.stringify(self.info);
      console.log("add info =" + params);
      var encrypt = Encrypt(params);
      self.$axios({
        method: "POST",
        url: "/api/ace-app/collection/addCollection",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log(response);
        self.clickSubmit = false;
        var data = response.data;
        var mydata = data.data;

        if (mydata == "Success") {
          self.$router.go(-1);
        } else if (mydata.code == "duplicateLogin") {
          self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
          self.isShowDialog = true;
          self.goToLogin = true;
        } else {
          self.clickSubmit = false;
          self.DialogContent = "提交失败，请重试";
          self.isShowDialog = true;
        }
      }).catch(function (error) {
        console.log(error);
        self.clickSubmit = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    }
  },
  mounted: function mounted() {
    var self = this;
    this.oneCase = this.$route.params.mycase;
    console.log(this.oneCase);
    this.scroll = new Bscroll(this.$refs.content); //定位

    plus.geolocation.getCurrentPosition(function (position) {
      var lat = position.coords.latitude; //纬度

      var long = position.coords.longitude; //经度

      var country = position.address.country; //国家

      var province = position.address.province; //省

      var city = position.address.city; //市

      var district = position.address.district; //区

      var street = position.address.street; //街

      var streetNum = position.address.streetNum; //街道号

      self.myAddress = country + province + city + district + street + streetNum;

      if (self.myAddress.length == 0) {
        self.myAddress = "定位失败";
      }
    }, function (error) {
      self.myAddress = "定位出错";
      console.log("定位出错：" + error.message);
    }, {});
  }
};
