import Search from "./Search";
import Bscroll from "better-scroll";
import DialogBar from "../../comment/DialogBar";
import Loading from "../../comment/Loading";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "Cases",
  data: function data() {
    return {
      showLoading: false,
      pageSize: 10,
      pageIndex: 1,
      cases: [],
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  props: {
    ishaveCases: Boolean
  },
  components: {
    Search: Search,
    DialogBar: DialogBar,
    Loading: Loading
  },
  methods: {
    cancel: function cancel() {
      this.isShowDialog = false;

      if (this.goToLogin) {
        var len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    searchMyCase: function searchMyCase(idNo) {
      if (idNo == null || idNo.length == 0) {
        this.DialogContent = "请输入身份证号码";
        this.isShowDialog = true;
      } else {
        this.pageIndex = 1;
        this.cases = [];
        localStorage.idNo = idNo;
        this.requestData(idNo);
      }
    },
    // 请求数据
    requestData: function requestData(idNo) {
      var self = this;
      var param = {
        idNo: "3412341966030360601",
        index: this.pageIndex,
        pageSize: this.pageSize
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      self.showLoading = true;
      self.$axios({
        method: "POST",
        url: "/api/ace-app/caseInfo/rmasInfo/query",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log("请求我的案件 成功");
        self.loginSucc(response);
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    loginSucc: function loginSucc(res) {
      //数据处理
      console.log(res);
      var self = this;
      var data = res.data;
      var mydata = data.data;

      if (mydata.code == "duplicateLogin") {
        self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
        self.isShowDialog = true;
        self.goToLogin = true;
        self.showLoading = false;
      } else {
        var list = mydata.list;

        if (this.pageIndex == 1) {
          self.cases = [];
          self.clearData();
        }

        if (list.length > 0) {
          for (var i = 0; i < list.length; i++) {
            self.cases.push(list[i]);
          } //保存当前界面的状态


          self.saveData(); //状态改变

          self.showLoading = false;
          self.$emit("getCases");
        } else {
          self.showLoading = false;
          self.DialogContent = "此身份证号暂无案件信息";
          self.isShowDialog = true;
        }
      }
    },
    gotoDetail: function gotoDetail(index) {
      this.$router.push({
        name: "caseDetail",
        params: {
          oneCase: this.cases[index],
          routerFrom: "Case"
        }
      });
    },
    saveData: function saveData() {
      localStorage.cases = JSON.stringify(this.cases);
      localStorage.pageIndex = this.pageIndex;
    },
    clearData: function clearData() {
      localStorage.removeItem("cases");
      localStorage.removeItem("pageIndex");
    }
  },
  watch: {
    ishaveCases: function ishaveCases(newVal) {
      var _this = this;

      var self = this;

      if (newVal === true) {
        this.$nextTick(function () {
          var bscrollDom = _this.$refs.cases;
          _this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });

          _this.scroll.on("pullingUp", function () {
            if (!_this.showLoading) {
              console.log("上拉加载");
              self.pageIndex++;
              self.requestData(localStorage.idNo);
            }

            _this.scroll.finishPullUp();
          });

          _this.scroll.on("pullingDown", function () {
            if (!_this.showLoading) {
              console.log("下拉刷新");
              self.pageIndex = 1;
              self.requestData(localStorage.idNo);
            }

            _this.scroll.finishPullDown();
          });
        });
      } else {
        self.clearData();
      }
    }
  },
  mounted: function mounted() {
    var mycases = localStorage.cases;

    if (mycases != null) {
      this.cases = JSON.parse(localStorage.cases);
      this.pageIndex = localStorage.pageIndex;
    }
  }
};
