import { Decrypt, Encrypt } from "../../../utils";
import photoUrl from "../../../../static/xiazai.png";
export default {
  name: "RmasAudio",
  data: function data() {
    return {
      urls: [],
      audioNames: [],
      audioLoaclUrls: [],
      downloadBtn: photoUrl
    };
  },
  props: {
    item: Object
  },
  beforeMount: function beforeMount() {
    if (this.item.audioList.length > 0) {
      this.urls = this.item.audioList.split(",");
      console.log("音频路径----- " + this.urls);

      for (var i = 0; i < this.urls.length; i++) {
        var oneUrl = this.urls[i];
        var names = oneUrl.split("/");
        var name = names[names.length - 1];
        this.audioNames.push(name);
      }
    }
  },
  methods: {
    downloadAudio: function downloadAudio(index) {
      this.$emit("downloading");
      var videoUrl = this.urls[index];
      var param = {
        url: videoUrl
      };
      console.log("开始下载录音 url= " + param);
      var encrypt = Encrypt(JSON.stringify(param));
      var self = this;
      var url = "api/ace-app/fileDownload/download?" + "parm=" + encodeURIComponent(encrypt);
      console.log("下载路径 url=" + url);
      self.$axios({
        method: "GET",
        url: url,
        responseType: "arraybuffer"
      }).then(function (res) {
        var blob = new Blob([res.data], {
          type: "application/vnd.openxmlformates-officedocument.spreadsheetml.sheet"
        });
        self.urls.splice(index, 1);
        self.audioLoaclUrls.push(URL.createObjectURL(blob));
        self.$emit("downloaded");
      }).catch(function (error) {
        console.log(error);
        self.$emit("downloadErr", "视频下载失败");
      });
    }
  }
};
