import Bscroll from "better-scroll";
import Loading from "../../comment/Loading";
import DialogBar from "../../comment/DialogBar";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "CheckWaiting",
  data: function data() {
    return {
      index: 1,
      pageSize: 10,
      checks: [],
      isHaveList: false,
      showLoading: false,
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  props: {
    isShow: Boolean
  },
  components: {
    Loading: Loading,
    DialogBar: DialogBar
  },
  methods: {
    cancel: function cancel() {
      this.isShowDialog = false;

      if (this.goToLogin) {
        var len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    passClick: function passClick(index) {
      var self = this;
      self.showLoading = true;
      var param = {
        caseSubId: self.checks[index].caseSubId,
        isPass: "Y"
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      self.$axios({
        method: "POST",
        url: "/api/ace-app/waitingCheck/rmasApplyCheck",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log(response);
        self.showLoading = false;
        var data = response.data;
        var mydata = data.data;

        if (mydata.code == "duplicateLogin") {
          self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
          self.isShowDialog = true;
          self.goToLogin = true;
        } else if (mydata == "success") {
          self.$router.push({
            name: "result",
            params: {
              isSucc: true,
              fromRouter: "check"
            }
          });
        } else {
          self.$router.push({
            name: "result",
            params: {
              isSucc: false,
              fromRouter: "check"
            }
          });
        }
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    unPassClick: function unPassClick(index) {
      var self = this;
      self.showLoading = true;
      var param = {
        caseSubId: self.checks[index].caseSubId,
        isPass: "N"
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      self.$axios({
        method: "POST",
        url: "/api/ace-app/waitingCheck/rmasApplyCheck",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log(response);
        self.showLoading = false;
        var data = response.data;
        var mydata = data.data;

        if (mydata.code == "duplicateLogin") {
          self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
          self.isShowDialog = true;
          self.goToLogin = true;
        } else if (mydata == "success") {
          self.$router.push({
            name: "result",
            params: {
              isSucc: true,
              fromRouter: "check"
            }
          });
        } else {
          self.$router.push({
            name: "result",
            params: {
              isSucc: false,
              fromRouter: "check"
            }
          });
        }
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    requestData: function requestData() {
      console.log("请求待审核");
      var self = this;
      self.showLoading = true;
      var param = {
        index: this.index,
        pageSize: this.pageSize
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      console.log("token =" + localStorage.token);
      self.$axios({
        method: "POST",
        url: "/api/ace-app/waitingCheck/rmasWaiting/query",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log(response);
        self.showLoading = false;
        var data = response.data;
        var mydata = data.data;

        if (mydata.code == "duplicateLogin") {
          self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
          self.isShowDialog = true;
          self.goToLogin = true;
        } else {
          var list = mydata.list;

          if (list.length == 0 && self.index == 1) {
            self.isHaveList = false;
          } else {
            self.isHaveList = true;

            if (self.index == 1) {
              self.checks = [];
            }

            if (list.length > 0) {
              for (var i = 0; i < list.length; i++) {
                self.checks.push(list[i]);
              }
            }
          }

          localStorage.checkwaiting = JSON.stringify(self.checks);
        }
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    getTime: function getTime(time) {
      if (time != null) {
        return time.substring(0, 4) + "年" + time.substring(4, 6) + "月" + time.substring(6, 8) + "日";
      } else {
        return "";
      }
    }
  },
  watch: {
    isShow: function isShow(newVal) {
      var _this = this;

      if (newVal === true) {
        var bscrollDom = this.$refs.checkWaiting;
        console.log(bscrollDom);
        this.scroll = new Bscroll(bscrollDom, {
          click: true,
          scrollY: true,
          pullUpLoad: {
            threshold: -30
          },
          pullDownRefresh: {
            threshold: 30,
            stop: 20
          }
        });
        this.scroll.on("pullingUp", function () {
          if (!_this.showLoading) {
            console.log("上拉加载");
            _this.pageIndex++;

            _this.requestData();
          }

          _this.scroll.finishPullUp();
        });
        this.scroll.on("pullingDown", function () {
          if (!_this.showLoading) {
            console.log("下拉刷新");
            _this.pageIndex = 1;

            _this.requestData();
          }

          _this.scroll.finishPullDown();
        });
        console.log("checkWaiting isShow true");
        this.requestData();
      }
    }
  },
  mounted: function mounted() {
    var _this2 = this;

    var bscrollDom = this.$refs.checkWaiting;
    console.log(bscrollDom);
    this.scroll = new Bscroll(bscrollDom, {
      click: true,
      scrollY: true,
      pullUpLoad: {
        threshold: -30
      },
      pullDownRefresh: {
        threshold: 30,
        stop: 20
      }
    });
    this.scroll.on("pullingUp", function () {
      if (!_this2.showLoading) {
        console.log("上拉加载");
        _this2.pageIndex++;

        _this2.requestData();
      }

      _this2.scroll.finishPullUp();
    });
    this.scroll.on("pullingDown", function () {
      if (!_this2.showLoading) {
        console.log("下拉刷新");
        _this2.pageIndex = 1;

        _this2.requestData();
      }

      _this2.scroll.finishPullDown();
    });
    console.log("checkWaiting isShow true");
    this.requestData();
  }
};
