import RmasInfo from "./RmasInfo";
import RmasPhoto from "./RmasPhoto";
import RmasVideo from "./RmasVideo";
import RmasAudio from "./RmasAudio";
import Bscroll from "better-scroll";
import Loading from "../../comment/Loading";
import DialogBar from "../../comment/DialogBar";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "rmaslist",
  data: function data() {
    return {
      showLoading: false,
      pageSize: 10,
      pageIndex: 1,
      rmasDatas: [],
      isHaveList: true,
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  props: {
    caseSubId: String
  },
  components: {
    RmasInfo: RmasInfo,
    RmasPhoto: RmasPhoto,
    RmasVideo: RmasVideo,
    RmasAudio: RmasAudio,
    Loading: Loading,
    DialogBar: DialogBar
  },
  methods: {
    // 请求数据
    requestData: function requestData() {
      console.log("请求催收记录" + this.caseSubId);
      var self = this;
      self.showLoading = true;
      var param = {
        caseSubId: self.caseSubId,
        index: self.pageIndex,
        pageSize: self.pageSize
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      this.$axios({
        method: "POST",
        url: "/api/ace-app/collection/rmasCollection/query",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        console.log(response);
        self.showLoading = false;

        if (self.pageIndex == 1) {
          self.rmasDatas = [];
        }

        var mydata = response.data;
        var mylist = mydata.data;
        var list = mylist.list;

        if (mylist.code == "duplicateLogin") {
          self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
          self.isShowDialog = true;
          self.goToLogin = true;
        } else if (list.length > 0) {
          for (var i = 0; i < list.length; i++) {
            var data = list[i];
            self.rmasDatas.push(data);
          }

          self.isHaveList = true;
        } else {
          self.isHaveList = self.rmasDatas.length > 0;
        }
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    cancel: function cancel() {
      this.isShowDialog = false;

      if (this.goToLogin) {
        var len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    downloading: function downloading() {
      this.showLoading = true;
    },
    downloaded: function downloaded() {
      this.showLoading = false;
    },
    downloadErr: function downloadErr(err) {
      this.showLoading = false;
      this.showLoading = false;
      this.DialogContent = err;
    }
  },
  mounted: function mounted() {
    var _this = this;

    this.requestData();
    this.scroll = new Bscroll(this.$refs.haveList, {
      click: true,
      scrollY: true,
      pullUpLoad: {
        threshold: -30
      },
      pullDownRefresh: {
        threshold: 30,
        stop: 20
      }
    });
    this.scroll.on("pullingUp", function () {
      if (!_this.showLoading) {
        console.log("上拉加载");
        _this.pageIndex++;

        _this.requestData();
      }

      _this.scroll.finishPullUp();
    });
    this.scroll.on("pullingDown", function () {
      if (!_this.showLoading) {
        console.log("下拉刷新");
        _this.pageIndex = 1;

        _this.requestData();
      }

      _this.scroll.finishPullDown();
    });
  }
};
