import Search from "./Search";
import Bscroll from "better-scroll";
export default {
  name: "Cases",
  props: {
    ishaveCases: Boolean
  },
  components: {
    Search: Search
  },
  methods: {
    searchMyCase: function searchMyCase(idNo) {
      console.log(idNo);
      this.ishaveCase = true;
      this.$emit("getCasesNum", 2);
    }
  },
  watch: {
    ishaveCases: function ishaveCases(newVal) {
      var _this = this;

      if (newVal === true) {
        this.$nextTick(function () {
          var bscrollDom = _this.$refs.cases;
          _this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });

          _this.scroll.on("pullingUp", function () {
            console.log("上拉加载");

            _this.scroll.finishPullUp();
          });

          _this.scroll.on("pullingDown", function () {
            console.log("下拉刷新");

            _this.scroll.finishPullDown();
          });
        });
      }
    }
  }
};
