export default {
  name: "AddVideo",
  data: function data() {
    return {
      videoList: [],
      videoFiles: [],
      videoUrls: [],
      upErr: false
    };
  },
  props: {
    beginUp: Boolean,
    caseSubId: String
  },
  watch: {
    beginUp: function beginUp(newVal) {
      var _this = this;

      if (newVal === true) {
        var succNum;

        (function () {
          var self = _this;
          self.upErr = false;

          if (_this.videoFiles.length > 0) {
            succNum = 0;
            console.log("开始上传视频");

            for (var i = 0; i < _this.videoFiles.length; i++) {
              if (self.upErr) break;
              var file = _this.videoFiles[i];
              var param = new FormData();
              param.append("file", file, file.name);
              var config = {
                headers: {
                  "Content-Type": "multipart/form-data"
                }
              };
              var url = "api/ace-app/collectionVideo/videoAdd?caseSubId=" + self.caseSubId;

              _this.$axios.post(url, param, config).then(function (response) {
                var mydata = response.data;

                if (mydata.message == "上传成功") {
                  succNum++; //保存返回的URL

                  var _url = mydata.data;
                  self.videoUrls.push(_url);

                  if (succNum == _this.videoFiles.length) {
                    //视频上传完
                    self.$emit("videoUpFinish", self.videoUrls);
                  }
                } else {
                  self.upErr = true;
                  self.$emit("videoUpErr");
                }
              }).catch(function (error) {
                console.log(error);
                self.upErr = true;
                self.$emit("videoUpErr");
              });
            }
          } else {
            self.$emit("videoUpFinish", self.videoUrls);
          }
        })();
      }
    }
  },
