    },
    fileDel: function fileDel(index) {
      var self = this;
      self.videoList.splice(index, 1);
    },
    fileChange: function fileChange(el) {
      var self = this;

      for (var i = 0; i < el.target.files.length; i++) {
        var file = el.target.files[i];
        console.log(file);
        var url = self.getFileURL(file);
        self.videoList.push(url);
        self.videoFiles.push(file);
      }
    },
    getFileURL: function getFileURL(file) {
      var getUrl = null;

      if (window.createObjectURL != undefined) {
        getUrl = window.createObjectURL(file);
      } else if (window.URL != undefined) {
        getUrl = window.URL.createObjectURL(file);
      } else if (window.webkitURL != undefined) {
        getUrl = window.webkitURL.createObjectURL(file);
      }

      return getUrl;
