import Search from "./Search";
import Bscroll from "better-scroll";
import Hint from "../../comment/Hint";
import Loading from "../../comment/Loading";
export default {
  name: "Cases",
  data: function data() {
    return {
      hintTitle: "",
      showHint: false,
      showLoading: false,
      timer: null,
      pageSize: 10,
      pageIndex: 0,
      cases: [{
        name: "烟岚",
        phone: "13788888888",
        homeAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
        workAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
        money: "1000000"
      }, {
        name: "烟岚",
        phone: "13788888888",
        homeAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
        workAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
        money: "1000000"
      }, {
        name: "烟岚",
        phone: "13788888888",
        homeAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
        workAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
        money: "1000000"
      }]
    };
  },
  props: {
    ishaveCases: Boolean
  },
  components: {
    Search: Search,
    Hint: Hint,
    Loading: Loading
  },
  methods: {
    searchMyCase: function searchMyCase(idNo) {
      if (idNo.length == 0) {
        this.hintTitle = "请输入身份证号码";
        this.showHint = true;
      } else {
        this.pageIndex = 0;
        localStorage.idNo = idNo;
        this.requestData(idNo);
      }
    },
    hideHint: function hideHint() {
      this.showHint = false;
    },
    // 请求数据
    requestData: function requestData(idNo) {
      var _this = this;

      this.showLoading = true;
      this.timer = setInterval(function () {
        _this.showLoading = false;
        clearInterval(_this.timer);

        if (idNo == "123456") {
          //没请求到数据
          _this.hintTitle = "该身份证无案件";
          _this.showHint = true;
        } else {
          //请求到数据
          _this.ishaveCase = true;

          _this.$emit("getCases");
        }
      }, 2000);
    },
    gotoDetail: function gotoDetail(index) {
      console.log("点击了第" + index + "个");
      this.$router.push({
        name: 'caseDetail',
        params: this.cases[index]
      });
    }
  },
  watch: {
    ishaveCases: function ishaveCases(newVal) {
      var _this2 = this;

      if (newVal === true) {
        this.$nextTick(function () {
          var bscrollDom = _this2.$refs.cases;
          _this2.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });

          _this2.scroll.on("pullingUp", function () {
            if (!_this2.showLoading) {
              console.log("上拉加载");
              _this2.pageIndex++;

              _this2.requestData(localStorage.idNo);
            }

            _this2.scroll.finishPullUp();
          });

          _this2.scroll.on("pullingDown", function () {
            if (!_this2.showLoading) {
              console.log("下拉刷新");
              _this2.pageIndex = 0;

              _this2.requestData(localStorage.idNo);
            }

            _this2.scroll.finishPullDown();
          });
        });
      }
    }
  }
};
