import photoUrl from "../../../../static/photo@2x.png";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "RmasPhoto",
  data: function data() {
    return {
      urls: [],
      photoUrl: photoUrl,
      testUrl: "",
      photoLocalUrls: [],
      photoNum: 0
    };
  },
  props: {
    item: Object,
    preview: String
  },
  beforeMount: function beforeMount() {
    this.urls = this.item.photoList.split(",");

    for (var i = 0; i < this.urls.length; i++) {
      this.downloadPhoto(this.urls[i]);
    }
  },
  methods: {
    downloadPhoto: function downloadPhoto(photoUrl) {
      var _this = this;

      this.$emit("downloading");
      console.log("请求下载图片 图片路径URL=" + photoUrl);
      var param = {
        url: photoUrl
      };
      var paramJson = JSON.stringify(param);
      console.log(paramJson);
      var encrypt = Encrypt(paramJson);
      var self = this;
      var url = "/api/ace-app/fileDownload/download?" + "parm=" + encodeURIComponent(encrypt);
      console.log("下载路径 url=" + url);
      self.$axios({
        method: "GET",
        url: url,
        responseType: "arraybuffer"
      }).then(function (res) {
        var blob = new Blob([res.data], {
          type: "application/vnd.openxmlformates-officedocument.spreadsheetml.sheet"
        });
        var localUrl = URL.createObjectURL(blob);
        console.log("图片下载后 本地路径 url=" + localUrl);
        self.photoLocalUrls.push(localUrl);
        self.photoNum++;

        if (self.photoNum == _this.urls.length) {
          self.$emit("downloaded");
        }
      }).catch(function (error) {
        console.log(error);
        self.$emit("downloadErr", '图片下载失败');
      });
    }
  }
};
