export default {
  name: "Hint",
  props: {
    title: String
  },
  data: function data() {
    return {
      inShowTime: true,
      timer: null
    };
  },
  methods: {
    getTime: function getTime() {
      var date = new Date();
      var seperator1 = "-";
      var year = date.getFullYear();
      var month = date.getMonth() + 1;
      var strDate = date.getDate();
      var hour = date.getHours();
      var min = date.getMinutes();
      var sec = date.getSeconds();

      if (month >= 1 && month <= 9) {
        month = "0" + month;
      }

      if (strDate >= 0 && strDate <= 9) {
        strDate = "0" + strDate;
      }

      var currentdate = year + seperator1 + month + seperator1 + strDate + " " + hour + ":" + min + ":" + sec;
      return currentdate;
    }
  },
  computed: {
    isShow: function isShow() {
      console.log("计时结束：" + this.getTime());
      return this.title.length && this.inShowTime;
    }
  },
  watch: {
    title: function title(curVal) {
      var _this = this;

      if (curVal.length > 0) {
        console.log("开始计时：" + this.getTime());
        this.timer = (function () {
          _this.inShowTime = false;
        }, 3000);
      }
    }
  },
  beforeDestroy: function beforeDestroy() {
    if (this.timer) {
      clearInterval(this.timer); //在vue实例销毁钱，清除我们的定时器
    }
  }
};
