import Bscroll from "better-scroll";
import Loading from "../../comment/Loading";
import DialogBar from "../../comment/DialogBar";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "Applys",
  data: function data() {
    return {
      showLoading: false,
      pageSize: 10,
      pageIndex: 1,
      applys: [],
      isHaveList: false,
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  props: {
    isShow: Boolean
  },
  components: {
    Loading: Loading,
    DialogBar: DialogBar
  },
  methods: {
    cancel: function cancel() {
      this.isShowDialog = false;

      if (this.goToLogin) {
        console.log("回到首页");
        var len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    requestData: function requestData() {
      console.log("请求我的代扣申请");
      var self = this;
      var param = {
        index: self.pageIndex,
        pageSize: self.pageSize
      };
      var params = JSON.stringify(param);
      var encrypt = Encrypt(params);
      self.showLoading = true;
      self.$axios({
        method: "POST",
        url: "/api/ace-app/holdApply/rmasWaiting/query",
        data: {
          params: encrypt,
          userId: localStorage.userId,
          token: localStorage.token
        }
      }).then(function (response) {
        self.loginSucc(response);
      }).catch(function (error) {
        console.log(error);
        self.showLoading = false;
        self.DialogContent = "系统繁忙，请稍等";
        self.isShowDialog = true;
      });
    },
    loginSucc: function loginSucc(response) {
      console.log("我的代扣申请 请求成功");
      console.log(response); //数据处理

      var self = this;
      var data = response.data;
      var mydata = data.data;
      console.log("mydata.code" + mydata.code);

      if (mydata.code == "duplicateLogin") {
        self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
        self.isShowDialog = true;
        self.goToLogin = true;
      } else {
        var list = mydata.list;

        if (this.pageIndex == 1) {
          this.applys = [];
          this.clearData();
        }

        if (list.length > 0) {
          for (var i = 0; i < list.length; i++) {
            self.applys.push(list[i]);
          } //存数据


          self.saveData();
          self.isHaveList = true;
        } else if (this.pageIndex == 1) {
          self.isHaveList = false;
        }
      } //状态改变


      self.showLoading = false;
    },
    saveData: function saveData() {
      localStorage.applys = JSON.stringify(this.applys);
      localStorage.applyIndex = this.pageIndex;
    },
    clearData: function clearData() {
      localStorage.removeItem("applys");
      localStorage.removeItem("applyIndex");
    },
    getTime: function getTime(time) {
      if (time != null) {
        return time.substring(0, 4) + "年" + time.substring(4, 6) + "月" + time.substring(6, 8) + "日";
      } else {
        return "";
      }
    }
  },
  watch: {
    isShow: function isShow(newVal) {
      var _this = this;

      if (newVal === true) {
        this.$nextTick(function () {
          var bscrollDom = _this.$refs.applys;
          _this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });

          _this.scroll.on("pullingUp", function () {
            if (!_this.showLoading) {
              console.log("上拉加载");
              _this.pageIndex++;

              _this.requestData(localStorage.idNo);
            }

            _this.scroll.finishPullUp();
          });

          _this.scroll.on("pullingDown", function () {
            if (!_this.showLoading) {
              console.log("下拉刷新");
              _this.pageIndex = 1;

              _this.requestData(localStorage.idNo);
            }

            _this.scroll.finishPullDown();
          });
        });
      }

      if (localStorage.applys == null) {
        this.requestData();
      } else {
        this.cases = JSON.parse(localStorage.applys);
        this.pageIndex = localStorage.applyIndex;
      }
    }
  },
  mounted: function mounted() {
    this.clearData();
  }
};
