import Bscroll from "better-scroll";
import Loading from "../../comment/Loading";
import DialogBar from "../../comment/DialogBar";
import { Decrypt, Encrypt } from "../../../utils";
export default {
  name: "Checked",
  data() {
    return {
      index: 1,
      pageSize: 10,
      isHaveList: false,
      checkeds: [],
      showLoading: false,
      isShowDialog: false,
      DialogContent: "",
      goToLogin: false
    };
  },
  components: {
    Loading,
    DialogBar
  },
  props: {
    isShow: Boolean
  },
  methods: {
    cancel() {
      this.isShowDialog = false;
      if (this.goToLogin) {
        let len = history.length;
        this.$router.go(-(len - 1));
      }
    },
    checkClick(index) {
      console.log("go to payStatus");
      this.$router.push({ name: "payStatus", params: this.checkeds[index] });
    },
    requestData() {
      console.log("请求已审核");
      let self = this;
      self.showLoading = true;
      let param = {
        index: this.index,
        pageSize: this.pageSize
      };
      let params = JSON.stringify(param);
      let encrypt = Encrypt(params);
      self
        .$axios({
          method: "POST",
          url: "/api/ace-app/waitingCheck/rmasChecked/query",
          data: {
            params: encrypt,
            userId: localStorage.userId,
            token: localStorage.token
          }
        })
        .then(function(response) {
          console.log(response);
          let data = response.data;
          let mydata = data.data;
          if (mydata.code == "duplicateLogin") {
            self.DialogContent = "用户已在另一设备上登录，请在此设备上重新登录";
            self.isShowDialog = true;
            self.goToLogin = true;
          } else {
            let list = mydata.list;
            if (list.length == 0 && self.index == 1) {
              self.isHaveList = false;
            } else {
              self.isHaveList = true;
              if (self.index == 1) {
                self.checkeds = [];
              }
              if (list.length > 0) {
                for (let i = 0; i < list.length; i++) {
                  self.checkeds.push(list[i]);
                }
              }
            }
          }
          self.showLoading = false;
        })
        .catch(function(error) {
          console.log(error);
          self.showLoading = false;
          self.DialogContent = "系统繁忙，请稍等";
          self.isShowDialog = true;
        });
    },
    getTime(time) {
      if (time != null) {
        return (
          time.substring(0, 4) +
          "年" +
          time.substring(4, 6) +
          "月" +
          time.substring(6, 8) +
          "日"
        );
      } else {
        return "";
      }
    }
  },
  watch: {
    isShow: function(newVal) {
      if (newVal === true) {
        this.$nextTick(() => {
          let bscrollDom = this.$refs.checked;
          this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });
          this.scroll.on("pullingUp", () => {
            if (!this.showLoading) {
              console.log("上拉加载");
              this.pageIndex++;
              this.requestData();
            }
            this.scroll.finishPullUp();
          });
          this.scroll.on("pullingDown", () => {
            if (!this.showLoading) {
              console.log("下拉刷新");
              this.pageIndex = 1;
              this.requestData();
            }
            this.scroll.finishPullDown();
          });
        });
        this.requestData();
      }
    }
  }
};
