import eyeOpen from "../../../../static/眼睛-开@2x.png";
import eyeClose from "../../../../static/眼睛-闭@2x.png";
import axios from "axios";
import Hint from "../../comment/Hint";
export default {
  name: "LoginContent",
  data() {
    return {
      isOpen: false,
      pwd: "",
      name: "",
      eyeImgUrl: eyeClose,
      hintTitle: ""
    };
  },
  components: {
    Hint
  },
  methods: {
    openCloseClick() {
      if (this.isOpen) {
        this.eyeImgUrl = eyeClose;
        this.isOpen = !this.isOpen;
        this.pwd = document.getElementsByClassName("pwdInput")[0].value;
      } else {
        this.eyeImgUrl = eyeOpen;
        this.isOpen = !this.isOpen;
        this.pwd = document.getElementsByClassName("pwdInput-pwd")[0].value;
      }
    },
    loginBtnClick() {
      if (this.name.length == 0) {
        this.hintTitle = "请输入姓名";
      } else if (this.pwd.length == 0) {
        console.log("请输入密码");
      } else {
        this.$router.push("/myCase");
        axios.get("/static/tsconfig.json").then(this.loginSucc);
      }
    },
    loginSucc(res) {
      console.log(res);
    }
  }
};
