          bankCard: "234593850504850359"
        }
      ]
    };
  },
  props: {
    isShow: Boolean
  },
  components: {
    Loading
  },
  methods: {
    requestData() {
      this.showLoading = true;
      this.timer = setInterval(() => {
        this.showLoading = false;
        clearInterval(this.timer);
        if (localStorage.idNo == "123456") {
          //没请求到数据
        } else {
          //请求到数据
        }
      }, 2000);
    }
  },
  watch: {
    isShow: function(newVal) {
      if (newVal === true) {
        this.$nextTick(() => {
          let bscrollDom = this.$refs.applys;
          this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });
          this.scroll.on("pullingUp", () => {
            if (!this.showLoading) {
              console.log("上拉加载");
              this.pageIndex++;
              this.requestData(localStorage.idNo);
            }
            this.scroll.finishPullUp();
          });
          this.scroll.on("pullingDown", () => {
            if (!this.showLoading) {
              console.log("下拉刷新");
              this.pageIndex = 0;
              this.requestData(localStorage.idNo);
            }
            this.scroll.finishPullDown();
          });
        });
      }
    }
  },
  mounted() {
    this.requestData();
  }
};
