import RmasInfo from "./RmasInfo";
import RmasPhoto from "./RmasPhoto";
import RmasVideo from "./RmasVideo";
import RmasAudio from "./RmasAudio";
import Bscroll from "better-scroll";
import Loading from "../../comment/Loading";
export default {
  name: "rmaslist",
  data() {
    return {
      showLoading: false,
      timer: null,
      pageSize: 10,
      pageIndex: 0
    };
  },
  props: {
    isHaveList: Boolean
  },
  components: {
    RmasInfo,
    RmasPhoto,
    RmasVideo,
    RmasAudio,
    Loading
  },
  methods: {
    // 请求数据
    requestData(idNo) {
      this.showLoading = true;
      this.timer = setInterval(() => {
        this.showLoading = false;
        clearInterval(this.timer);
        if (idNo == "123456") {
          //没请求到数据
        } else {
          //请求到数据
        }
      }, 2000);
    }
  },
  mounted() {
    this.scroll = new Bscroll(this.$refs.haveList, {
      click: true,
      scrollY: true,
      pullUpLoad: {
        threshold: -30
      },
      pullDownRefresh: {
        threshold: 30,
        stop: 20
      }
    });
    this.scroll.on("pullingUp", () => {
      if (!this.showLoading) {
        console.log("上拉加载");
        this.pageIndex++;
        this.requestData(localStorage.idNo);
      }
      this.scroll.finishPullUp();
    });
    this.scroll.on("pullingDown", () => {
      if (!this.showLoading) {
        console.log("下拉刷新");
        this.pageIndex = 0;
        this.requestData(localStorage.idNo);
      }
      this.scroll.finishPullDown();
    });
  }
};
