export default {
  name: "AddVideo",
  data() {
    return {
      videoList: []
    };
  },
  methods: {
    //摄像
    getVideo() {
      let self = this;
      var cmr = plus.camera.getCamera();
      var res = cmr.supportedVideoResolutions[0];
      var fmt = cmr.supportedVideoFormats[0];
      cmr.startVideoCapture(
        function(path) {
          plus.io.resolveLocalFileSystemURL(
            path,
            function(entry) {
              console.log(entry.toLocalURL() + " name=" + entry.name);
              self.videoList.push(entry.toLocalURL());
            },
            function(e) {
              console.log("读取拍照文件错误" + e.message);
              plus.nativeUI.toast("读取拍照文件错误：" + e.message);
            }
          );
        },
        function(error) {
          console.log("拍摄失败" + error.message);
        },
        { resolution: res, format: fmt }
      );
    },
    //从相册选
    galleryVideo() {
      let self = this;
      plus.gallery.pick(
        function(event) {
          console.log("选择视频成功");
          for (var i in event.files) {
            console.log(event.files[i]);
            self.videoList.push(event.files[i]);
          }
        },
        function(error) {
          console.log("选择视频失败" + error.message);
        },
        {
          filter: "video",
          multiple: true,
          system: true
        }
      );
    },
    showActionSheet() {
      let self = this;
      let bts = [
        {
          title: "摄像"
        },
        {
          title: "从相册选择"
        }
      ];
      plus.nativeUI.actionSheet(
        {
          cancel: "取消",
          buttons: bts
        },
        function(e) {
          if (e.index == 1) {
            self.getVideo();
          } else if (e.index == 2) {
            self.galleryVideo();
          }
        }
      );
    },
    fileDel(index) {
      let self = this;
      self.videoList.splice(index, 1);
    }
  },
  computed: {
    showVideo() {
      return this.videoList.length > 0;
    }
  }
};
