import Search from "./Search";
import Bscroll from "better-scroll";
import Hint from "../../comment/Hint";
import Loading from "../../comment/Loading";
export default {
  name: "Cases",
  data() {
    return {
      hintTitle: "",
      showHint: false,
      showLoading: false,
      timer: null,
      pageSize: 10,
      pageIndex: 0,
      cases: [
        {
          name: "烟岚",
          phone: "13788888888",
          homeAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
          workAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
          money: "1000000"
        },
        {
          name: "烟岚",
          phone: "13788888888",
          homeAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
          workAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
          money: "1000000"
        },
        {
          name: "烟岚",
          phone: "13788888888",
          homeAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
          workAdd: "安徽省合肥市庐阳区祥源广场A座12楼",
          money: "1000000"
        }
      ]
    };
  },
  props: {
    ishaveCases: Boolean
  },
  components: {
    Search,
    Hint,
    Loading
  },
  methods: {
    searchMyCase(idNo) {
      if (idNo.length == 0) {
        this.hintTitle = "请输入身份证号码";
        this.showHint = true;
      } else {
        this.pageIndex = 0;
        localStorage.idNo = idNo;
        this.requestData(idNo);
      }
    },
    hideHint() {
      this.showHint = false;
    },
    // 请求数据
    requestData(idNo) {
      this.showLoading = true;
      this.timer = setInterval(() => {
        this.showLoading = false;
        clearInterval(this.timer);
        if (idNo == "123456") {
          //没请求到数据
          this.hintTitle = "该身份证无案件";
          this.showHint = true;
        } else {
          //请求到数据
          this.ishaveCase = true;
          this.$emit("getCases");
        }
      }, 2000);
    },
    gotoDetail(index) {
      console.log("点击了第" + index + "个");
      this.$router.push({name:'caseDetail',params:this.cases[index]})
    }
  },
  watch: {
    ishaveCases: function(newVal) {
      if (newVal === true) {
        this.$nextTick(() => {
          let bscrollDom = this.$refs.cases;
          this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });
          this.scroll.on("pullingUp", () => {
            if (!this.showLoading) {
              console.log("上拉加载");
              this.pageIndex++;
              this.requestData(localStorage.idNo);
            }
            this.scroll.finishPullUp();
          });
          this.scroll.on("pullingDown", () => {
            if (!this.showLoading) {
              console.log("下拉刷新");
              this.pageIndex = 0;
              this.requestData(localStorage.idNo);
            }
            this.scroll.finishPullDown();
          });
        });
      }
    }
  }
};
