export default {
  name: "AddAudio",
  data() {
    return {
      audioList: []
    };
  },
  methods: {
    //录音
    getAudio() {
      var rec = plus.audio.getRecorder();
      console.log("获取到的路由设备" + rec);
      rec.record(
        {},
        function(path) {
          console.log("录音成功");
          plus.io.resolveLocalFileSystemURL(
            path,
            function(entry) {
              console.log(entry.toLocalURL() + " name=" + entry.name);
              self.audioList.push(entry.toLocalURL());
            },
            function(e) {
              console.log("读取拍照文件错误" + e.message);
              plus.nativeUI.toast("读取拍照文件错误：" + e.message);
            }
          );
        },
        function(error) {
          console.log("录音失败");
        }
      );
    },
    galleryAuido() {
      let self = this;
      plus.gallery.pick(
        function(event) {
          console.log("选择音频成功");
          for (var i in event.files) {
            console.log(event.files[i]);
            self.audioList.push(event.files[i]);
          }
        },
        function(error) {
          console.log("选择音频失败" + error.message);
        },
        {
          filter: "video",
          multiple: true,
          system: true
        }
      );
    },
    showActionSheet() {
      let osName = plus.os.name;
      if (osName === "iOS") {
        this.showActionSheet_IOS();
      } else {
        this.showActionSheet_Android();
      }
    },
    showActionSheet_IOS() {
      let self = this;
      let bts = [
        {
          title: "从相册选择"
        }
      ];
      plus.nativeUI.actionSheet(
        {
          cancel: "取消",
          buttons: bts
        },
        function(e) {
          if (e.index == 1) {
            self.galleryAuido();
          }
        }
      );
    },
    showActionSheet_Android() {
      let self = this;
      let bts = [
        {
          title: "录音"
        },
        {
          title: "从相册选择"
        }
      ];
      plus.nativeUI.actionSheet(
        {
          cancel: "取消",
          buttons: bts
        },
        function(e) {
          if (e.index == 1) {
            self.getAudio();
          } else if (e.index == 2) {
            self.galleryAuido();
          }
        }
      );
    },
    fileDel(index) {
      let self = this;
      self.audioList.splice(index, 1);
    }
  },
  computed: {
    showAudio() {
      return this.audioList.length > 0;
    }
  }
};
