import Search from "./Search";
import Bscroll from "better-scroll";
export default {
  name: "Cases",
  props: {
    ishaveCases: Boolean
  },
  components: {
    Search
  },
  methods: {
    searchMyCase(idNo) {
      console.log(idNo);
      this.ishaveCase = true;
      this.$emit("getCasesNum", 2);
    }
  },
  watch: {
    ishaveCases: function(newVal) {
      if (newVal === true) {
        this.$nextTick(() => {
          let bscrollDom = this.$refs.cases;
          this.scroll = new Bscroll(bscrollDom, {
            click: true,
            scrollY: true,
            pullUpLoad: {
              threshold: -30
            },
            pullDownRefresh: {
              threshold: 30,
              stop: 20
            }
          });
          this.scroll.on("pullingUp", () => {
            console.log("上拉加载");
            this.scroll.finishPullUp();
          });
          this.scroll.on("pullingDown", () => {
            console.log("下拉刷新");
            this.scroll.finishPullDown();
          });
        });
      }
    }
  }
};
